INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442814, 'Irinotecan', 'Phenolphthalein', 'Moderate', 'Because irinotecan has been reported to cause diarrhea in the majority of treated patients, the use of laxatives may not be appropriate during irinotecan therapy.', 'DDInter', 1767317476, 'In patients requiring laxatives for constipation associated with narcotic administration, the standard regimen may be continued under supervision of the clinician. However, patients should be advised to inform their physician of any changes in their bowel habit so that dosage adjustments or discontinuation of laxatives may be done promptly.', 'Synergism', 'In patients requiring laxatives for constipation associated with narcotic administration, the standard regimen may be continued under supervision of the clinician.', NULL, NULL, 'Moderate', 'pharmacodynamic');
