INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442851, 'Irinotecan', 'Sorafenib', 'Moderate', 'Coadministration with sorafenib may increase the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. The proposed mechanism is sorafenib inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of irinotecan and SN-38, respectively.', 'DDInter', 1767317476, 'Sorafenib product labeling recommends caution during coadministration with irinotecan. High plasma levels of irinotecan and SN-38 may result in increased toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'Metabolism', 'Sorafenib product labeling recommends caution during coadministration with irinotecan.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442852, 'Irinotecan', 'Sparfloxacin', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442853, 'Irinotecan', 'St. John''s Wort', 'Major', 'St. John''s wort may significantly reduce the antineoplastic effectiveness of irinotecan. The mechanism is induction of CYP450 3A4, the isoenzyme that is partially responsible for the metabolic clearance of irinotecan.', 'DDInter', 1767317476, 'The use of St. John''s wort should be avoided before and during the entire course of chemotherapy. It is not known how much time should elapse between the discontinuation of St. John''s wort and initiation of chemotherapy.', 'Metabolism', 'The use of St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1442854, 'Irinotecan', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 1767317476, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors. Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
