INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443008, 'Isavuconazonium', 'Aminoglutethimide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Caution is advised during concomitant use of isavuconazonium sulfate with CYP450 3A4 inducers. The potential for decreased efficacy of isavuconazonium sulfate should be considered.', 'Metabolism', 'Caution is advised during concomitant use of isavuconazonium sulfate with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443009, 'Isavuconazonium', 'Amprenavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated. Ritonavir given at low dosages as a pharmacokinetic booster may be used with caution, but is contraindicated at high dosages (e.g., 400 mg every 12 hours).', 'Metabolism', 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443010, 'Isavuconazonium', 'Artesunate', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), such as dabigatran. The proposed mechanism is isavuconazole inhibition of P-gp-mediated efflux of these drugs.', 'DDInter', 1767317476, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever isavuconazonium sulfate is added to or withdrawn from therapy with these drugs. Patients receiving concomitant dabigatran should be monitored closely for the development of bleeding complications and the dosage of dabigatran adjusted as necessary.', 'Absorption', 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
