INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443044, 'Isavuconazonium', 'Etravirine', 'Moderate', 'Coadministration of etravirine with a drug, such as isavuconazonium sulfate (prodrug of isavuconazole), that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of isavuconazole. Etravirine itself is a substrate and inducer of CYP450 3A4. Theoretically, etravirine may induce metabolism of isavuconazole while its own metabolism may be inhibited by the isavuconazole.', 'DDInter', 1767317476, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with isavuconazonium sulfate. In addition, the potential for decreased efficacy of isavuconazole should be considered. Dosage adjustments may be required for one or both drugs.', 'Metabolism', 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with isavuconazonium sulfate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443045, 'Isavuconazonium', 'Everolimus', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycaemia, and hyperlipidemia may be increased.', 'DDInter', 1767317476, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors. The manufacturer''s drug product labeling should be consulted for specific recommendations.', 'Metabolism, Absorption', 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443046, 'Isavuconazonium', 'Fedratinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, peripheral edema, hypokalemia, hypomagnesemia, and hepatotoxicity. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored, as isavuconazole itself is reportedly a moderate CYP450 3A4 inhibitor.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
