INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443167, 'Isavuconazonium', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated. Ritonavir given at low dosages as a pharmacokinetic booster may be used with caution, but is contraindicated at high dosages (e.g., 400 mg every 12 hours).', 'Metabolism', 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
