INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443171, 'Isavuconazonium', 'Prednisone', 'Moderate', 'Coadministration of isavuconazonium sulfate (prodrug of isavuconazole), a substrate as well as a moderate inhibitor of CYP450 3A4/5, with drugs that are both a substrate as well as inducer of CYP450 3A4/5 may result in decreased plasma concentrations of isavuconazole and increased plasma concentrations of the other drug. Isavuconazole may inhibit the metabolism of the coadministered drug while its own metabolism may be induced by the coadministered drug.', 'DDInter', 1767317476, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are both a substrate and inducer of CYP450 3A4/5, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever these drugs are added to or withdrawn from combination therapy.', 'Metabolism', 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are both a substrate and inducer of CYP450 3A4/5, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
