INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443179, 'Isavuconazonium', 'Quinine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors. Patients should be monitored closely for adverse reactions associated with quinine such as hematologic toxicities and cardiac arrhythmias including torsade de pointes and atrial fibrillation. Patients should be advised to contact their physician if they experience increased side effects such as headache, flushing, sweating, nausea, vomiting, diarrhea, abdominal pain, tinnitus, dizziness, vertigo, hearing impairment, blurred vision, vision impairment, and irregular heart rhythm.', 'Metabolism', 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443180, 'Isavuconazonium', 'Ramelteon', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', 'DDInter', 1767317476, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443181, 'Isavuconazonium', 'Ranolazine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 1767317476, 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443182, 'Isavuconazonium', 'Repaglinide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of repaglinide, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, pharmacologic response to repaglinide should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations.', 'Metabolism', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, pharmacologic response to repaglinide should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443183, 'Isavuconazonium', 'Retapamulin (topical)', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme. However, dosage adjustments are not necessary due to the low systemic exposure to retapamulin following topical application.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443184, 'Isavuconazonium', 'Ribociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, peripheral edema, hypokalemia, hypomagnesemia, and hepatotoxicity. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored, as isavuconazole itself is reportedly a moderate CYP450 3A4 inhibitor.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443185, 'Isavuconazonium', 'Rifampicin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
