INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443252, 'Isavuconazonium', 'Voriconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317476, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated. Ritonavir given at low dosages as a pharmacokinetic booster may be used with caution, but is contraindicated at high dosages (e.g., 400 mg every 12 hours).', 'Metabolism', 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443253, 'Isavuconazonium', 'Voxelotor', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the systemic exposure (AUC) to voxelotor, which has been shown to be a substrate of this isoenzyme.', 'DDInter', 1767317476, 'Caution is recommended if voxelotor is to be used with a CYP450 3A4 inhibitor. Dose adjustments may be necessary. Patients should be closely monitored for signs of voxelotor-related adverse effects, including headache, diarrhea, abdominal pain, nausea, and vomiting.', 'Metabolism', 'Caution is recommended if voxelotor is to be used with a CYP450 3A4 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443254, 'Isavuconazonium', 'Zanubrutinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme. Increased zanubrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 1767317476, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg twice daily when coadministered with moderate CYP450 3A4 inhibitors. Patients should be closely monitored for development of zanubrutinib-related toxicities, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. Following discontinuation of the CYP450 3A4 inhibitor, the previous dosage of zanubrutinib should be resumed.', 'Metabolism', 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg twice daily when coadministered with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443255, 'Isavuconazonium', 'Ziprasidone', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443256, 'Isavuconazonium', 'Zolpidem', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', 'DDInter', 1767317476, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443257, 'Isavuconazonium', 'Zonisamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zonisamide, which is metabolized by the isoenzyme. While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of zonisamide should be considered.', 'DDInter', 1767317476, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience symptoms such as severe drowsiness, confusion, loss of coordination, depression, aphasia, decreased sweating, fever, loss of seizure control, or slow pulse.', 'Metabolism', 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443258, 'Isocarboxazid', 'Oxitriptan', 'Major', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs) may potentiate the pharmacologic activity of serotonergic agents such as serotonin reuptake inhibitors, 5-HT1 receptor agonists, ergot alkaloids, buspirone, dextromethorphan, and most antidepressants. The result may be an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 1767317476, 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with serotonergic agents.', 'Synergism', 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443259, 'Isocarboxazid', 'Acetohexamide', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443260, 'Isocarboxazid', 'Acrivastine', 'Moderate', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and antihistamines may result in additive central nervous system depressant effects. In addition, limited data suggest that MAOIs may potentiate and prolong the anticholinergic effects of antihistamines due to inhibition of catecholamine degradation, which may lead to overstimulation of the sympathetic nervous system.', 'DDInter', 1767317476, 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine) should generally be avoided. If concomitant treatment is unavoidable, patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443261, 'Isocarboxazid', 'Amoxapine', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and dibenzazepine derivatives (e.g., tricyclic and tetracyclic antidepressants, cyclobenzaprine, carbamazepine) may produce significant adverse reactions including nausea, vomiting, flushing, dizziness, tremor, myoclonus, rigidity, diaphoresis, hyperthermia, autonomic instability, hypertensive crises, disseminated intravascular coagulation, severe convulsive seizures, coma, and death. The exact mechanism of interaction is unknown, but may involve excessive serotonergic activity in the central nervous system (i.e., serotonin syndrome).', 'DDInter', 1767317476, 'In general, dibenzazepine derivatives should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with tricyclic antidepressants, and vice versa.', 'Others', 'In general, dibenzazepine derivatives should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443262, 'Isocarboxazid', 'Amyl Nitrite', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 1767317476, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
