INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443268, 'Isocarboxazid', 'Butabarbital', 'Moderate', 'Limited clinical data suggest that nonselective monoamine oxidase inhibitors (MAOIs) may enhance or prolong the central nervous system effects of barbiturates. The mechanism of interaction is unknown, but may involve inhibition of barbiturate metabolism by MAOIs. Somnolence, dizziness, ataxia, and semi-consciousness have been reported.', 'DDInter', 1767317476, 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', 'Metabolism', 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443269, 'Isocarboxazid', 'Pentoxyverine', 'Major', 'Labelings for products containing carbetapentane state that the drug should not be used in combination with monoamine oxidase inhibitors (MAOIs). No data are available regarding a potential interaction. However, serious and fatal reactions have been associated with another cough suppressant, dextromethorphan, during coadministration with MAOIs. Dextromethorphan is a weak serotonin reuptake inhibitor whose serotonergic activity may be enhanced by MAOIs. The result is an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A receptors and characterized by symptoms such as CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia.', 'DDInter', 1767317476, 'Products containing carbetapentane should not be used concurrently with MAOIs or for 14 days after stopping treatment with an MAOI.', 'Synergism', 'Products containing carbetapentane should not be used concurrently with MAOIs or for 14 days after stopping treatment with an MAOI.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443270, 'Isocarboxazid', 'Carteolol', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 1767317476, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
