INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443370, 'Isocarboxazid', 'Levosalbutamol', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) can potentiate the cardiovascular adverse effects of beta-2 adrenergic agonists such as hypertension, palpitation, tachycardia, and chest pain.', 'DDInter', 1767317476, 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). Preferably, at least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with beta-2 agonists.', 'Synergism', 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443371, 'Isocarboxazid', 'Levamlodipine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 1767317476, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443372, 'Isocarboxazid', 'Levmetamfetamine (nasal)', 'Moderate', 'By inhibiting the breakdown of catecholamines, monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of systemically absorbed sympathomimetic agents following topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors).', 'DDInter', 1767317476, 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
