        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1443467, 'Isocarboxazid', 'Phenelzine', 'Major', 'The concomitant use of monoamine oxidase inhibitors (MAOIs) with other MAOIs may result in hypertensive crisis, convulsions, fever, delirium, coma, and circulatory collapse. Cases of hypertension with fatal and nonfatal stroke have been reported in patients who were switched from phenelzine or isocarboxazid to tranylcypromine. The mechanism is unknown.', NULL, 'Administration of MAOIs with other MAOIs concurrently or in rapid succession is considered contraindicated.', NULL, 'Administration of MAOIs with other MAOIs concurrently or in rapid succession is considered contraindicated. A washout period of at least 7 to 10 days is recommended when switching from one MAOI to another.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
