INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443767, 'Isoprenaline', 'Phendimetrazine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', 'DDInter', 1767317476, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443768, 'Isoprenaline', 'Phenelzine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) can potentiate the cardiovascular adverse effects of beta-2 adrenergic agonists such as hypertension, palpitation, tachycardia, and chest pain.', 'DDInter', 1767317476, 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). Preferably, at least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with beta-2 agonists.', 'Synergism', 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443769, 'Isoprenaline', 'Phenylephrine (nasal)', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 1767317476, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
