INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443788, 'Isoprenaline', 'Propranolol', 'Moderate', 'Some beta-blockers may antagonize the bronchodilatory, hypotensive, and tachycardic effects of isoproterenol. The mechanism is blockade of beta-adrenergic receptors, which leads to bronchoconstriction, vasodilation, and increased heart rate. Beta-blockers have been used successfully to treat catecholamine or isoproterenol-induced tachyarrhythmias.', 'DDInter', 1767317476, 'This combination should generally be avoided. Patients who are receiving isoproterenol for cardiac conditions should be closely monitored for adequate therapeutic effect if a beta blocker is added. If no alternative exists, small doses of a B-1 selective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, or metoprolol) may be preferable; however, respiratory status should be closely monitored in patients with obstructive pulmonary disease.', 'Antagonism', 'This combination should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443789, 'Isoprenaline', 'Protriptyline', 'Moderate', 'The concomitant use of isoproterenol and tricyclic antidepressants may increase the risk of arrhythmias, especially at high doses. The mechanism has not been delineated. Tachycardia has been reported in clinical studies.', 'DDInter', 1767317476, 'Some authorities suggest that the initial rate and dose of isoproterenol should be reduced in patients taking tricyclic antidepressants, and then titrated, as is usual according to clinical response to avoid the risk of developing hypertension or arrhythmias. Patients receiving this combination should be monitored and advised to notify their physician if they experience a fast or irregular pulse.', 'Synergism', 'Some authorities suggest that the initial rate and dose of isoproterenol should be reduced in patients taking tricyclic antidepressants, and then titrated, as is usual according to clinical response to avoid the risk of developing hypertension or arrhythmias.', NULL, NULL, 'Moderate', 'pharmacodynamic');
