INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443816, 'Isoprenaline', 'Sotalol', 'Moderate', 'Some beta-blockers may antagonize the bronchodilatory, hypotensive, and tachycardic effects of isoproterenol. The mechanism is blockade of beta-adrenergic receptors, which leads to bronchoconstriction, vasodilation, and increased heart rate. Beta-blockers have been used successfully to treat catecholamine or isoproterenol-induced tachyarrhythmias.', 'DDInter', 1767317476, 'This combination should generally be avoided. Patients who are receiving isoproterenol for cardiac conditions should be closely monitored for adequate therapeutic effect if a beta blocker is added. If no alternative exists, small doses of a B-1 selective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, or metoprolol) may be preferable; however, respiratory status should be closely monitored in patients with obstructive pulmonary disease.', 'Antagonism', 'This combination should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
