INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443925, 'Isosorbide dinitrate', 'Selexipag', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 1767317476, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443926, 'Isosorbide dinitrate', 'Sildenafil', 'Major', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of organic nitrates. Severe hypotension, syncope, or myocardial ischemia may result from use of the combination. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE5 inhibitors prevent degradation of cGMP while nitrates promote its synthesis.', 'DDInter', 1767317476, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated. A suitable time interval following sildenafil or vardenafil use for the safe administration of nitrates has not been determined. For patients treated with avanafil or tadalafil, at least 12 hours after the last dose of avanafil and 48 hours after the last dose of tadalafil are recommended before nitrate administration, should it be deemed medically necessary in a life-threatening situation. Even then, nitrates should only be administered under close medical supervision with appropriate hemodynamic monitoring. Patients should be advised to seek immediate medical attention if they experience anginal chest pain after taking a PDE5 inhibitor.', 'Synergism', 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1443927, 'Isosorbide dinitrate', 'Tadalafil', 'Major', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of organic nitrates. Severe hypotension, syncope, or myocardial ischemia may result from use of the combination. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE5 inhibitors prevent degradation of cGMP while nitrates promote its synthesis.', 'DDInter', 1767317476, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated. A suitable time interval following sildenafil or vardenafil use for the safe administration of nitrates has not been determined. For patients treated with avanafil or tadalafil, at least 12 hours after the last dose of avanafil and 48 hours after the last dose of tadalafil are recommended before nitrate administration, should it be deemed medically necessary in a life-threatening situation. Even then, nitrates should only be administered under close medical supervision with appropriate hemodynamic monitoring. Patients should be advised to seek immediate medical attention if they experience anginal chest pain after taking a PDE5 inhibitor.', 'Synergism', 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
