INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1444013, 'Hydralazine', 'Metoprolol', 'Minor', 'Hydralazine may increase serum levels of some oral beta blockers. The proposed mechanism is reduced hepatic blood flow resulting in decreased biotransformation of beta blockers that undergo extensive first-pass metabolism. The interaction is less likely to occur with sustained-release preparations. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact. This combination generally tends to have beneficial effects and is used commonly in the treatment of hypertension. The patient''s clinical response should be monitored. A dosage adjustment for the beta blocker may be required when given concomitantly with hydralazine.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
