        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1444548, 'Itraconazole', 'Fexofenadine', 'Minor', 'Coadministration with ketoconazole has been shown to significantly increase the oral bioavailability of fexofenadine. The proposed mechanism is ketoconazole inhibition of the intestinal efflux of fexofenadine via P-glycoprotein transporter. No studies have been performed using other azole antifungal agents, but similar results are possible given their similarities to ketoconazole. Fexofenadine had no effect on the pharmacokinetics of ketoconazole.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
