    ) VALUES (1444599, 'Itraconazole', 'Levomilnacipran', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of levomilnacipran, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of levomilnacipran should not exceed 80 mg once daily when used with potent CYP450 3A4 inhibitors.', NULL, 'The dosage of levomilnacipran should not exceed 80 mg once daily when used with potent CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
