        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1444653, 'Itraconazole', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of paclitaxel, which has been found in vitro to be a substrate of these isoenzymes.', NULL, 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', NULL, 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1444654, 'Itraconazole', 'Palbociclib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leukopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis may be increased.', NULL, 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider reducing the palbociclib dosage to 75 mg once daily. Following discontinuation of the potent CYP450 3A4 inhibitor, the palbociclib dosage should be returned (after 3 to 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
