    ) VALUES (1444681, 'Itraconazole', 'Prucalopride', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of prucalopride, which is thought to be a weak substrate of the efflux transporter.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
