    ) VALUES (1444719, 'Itraconazole', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: The dissolution of itraconazole requires an acidic environment. Antacids and some aluminum, calcium, or magnesium containing salts decrease the acidity of the stomach and can reduce the bioavailability and subsequent therapeutic effect of itraconazole.', NULL, 'Separation of doses by two or more hours is recommended.', NULL, 'Separation of doses by two or more hours is recommended.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
