        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1444720, 'Itraconazole', 'Sodium zirconium cyclosilicate', 'Moderate', 'INTERVAL: Because sodium zirconium cyclosilicate can transiently increase gastric pH, it may affect the absorption of coadministered medications that exhibit pH-dependent solubility. Altered efficacy or safety of these medications may occur when they are administered too close to the dosing of sodium zirconium cyclosilicate.', NULL, 'In general, concomitant oral medications should be administered at least 2 hours before or 2 hours after sodium zirconium cyclosilicate.', NULL, 'In general, concomitant oral medications should be administered at least 2 hours before or 2 hours after sodium zirconium cyclosilicate. Separation of dosing times is not needed if it has been determined that the concomitant medication does not exhibit pH-dependent solubility.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
