    ) VALUES (1444777, 'Itraconazole', 'Zanubrutinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg once daily when coadministered with potent CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg once daily when coadministered with potent CYP450 3A4 inhibitors. Patients should be closely monitored for development of zanubrutinib-related toxicities, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. Following discontinuation of the CYP450 3A4 inhibitor, the previous dosage of zanubrutinib should be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
