        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445883, 'Aminoglutethimide', 'Warfarin', 'Moderate', 'Aminoglutethimide may significantly increase warfarin clearance by induction of CYP450 2C9 and/or 3A4 hepatic microsomal enzymes. A similar effect may occur with other oral anticoagulants.', NULL, 'The patient should be monitored for reduced anticoagulant effect when aminoglutethimide is added, discontinued, or the dosage is changed.', NULL, 'The patient should be monitored for reduced anticoagulant effect when aminoglutethimide is added, discontinued, or the dosage is changed. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
