        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445894, 'Boceprevir', 'Warfarin', 'Moderate', 'Liver function changes due to treatment with direct-acting HCV antiviral agents may affect the therapeutic efficacy of vitamin K antagonists. The mechanism may involve potential pharmacokinetic changes for some vitamin K antagonists due to altered metabolism, or patients may exhibit alterations in hepatic synthesis of vitamin K-dependent coagulation factors and clearance of activated factors that could impact response to vitamin K antagonists.', NULL, 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', NULL, 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
