    ) VALUES (1445913, 'Cisapride', 'Warfarin', 'Moderate', 'Cisapride may enhance the effects of oral anticoagulants. The mechanism is unknown.', NULL, 'It is recommended that the INR or PT be monitored if cisapride is added, cisapride is discontinued, or the dosage is adjusted in a patient receiving anticoagulants.', NULL, 'It is recommended that the INR or PT be monitored if cisapride is added, cisapride is discontinued, or the dosage is adjusted in a patient receiving anticoagulants. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
