        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445922, 'Dextrothyroxine', 'Warfarin', 'Moderate', 'Dextrothyroxine may enhance the hypoprothrombinemic response to oral anticoagulants. The mechanism is unknown.', NULL, 'If dextrothyroxine and an oral anticoagulant must be used together, the patient should be closely monitored for excessive anticoagulation, and the oral anticoagulant dosage should be reduced as needed.', NULL, 'If dextrothyroxine and an oral anticoagulant must be used together, the patient should be closely monitored for excessive anticoagulation, and the oral anticoagulant dosage should be reduced as needed. Patients should be advised to notify their physician if they experience any signs of excessive anticoagulation, such as unusual or prolonged bleeding, bruising, vomiting, change in stool or urine color, headache, dizziness, or weakness.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
