        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445923, 'Dicloxacillin', 'Warfarin', 'Major', 'Dicloxacillin and nafcillin may diminish the hypoprothrombinemic effect of warfarin. The exact mechanism of interaction is unknown but may involve induction of hepatic warfarin metabolism by these penicillins.', NULL, 'Coagulation parameters should be monitored more closely during coadministration of warfarin with dicloxacillin or nafcillin and for several weeks after discontinuation of the antibiotic.', NULL, 'Coagulation parameters should be monitored more closely during coadministration of warfarin with dicloxacillin or nafcillin and for several weeks after discontinuation of the antibiotic. The same precaution may be applicable during therapy with other oral anticoagulants.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
