        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445935, 'Eptifibatide', 'Warfarin', 'Major', 'The risk of bleeding, including major bleeding (intracranial hemorrhage or a decrease in hemoglobin greater than 5 mg/dl) is significantly more likely when oral anticoagulants and glycoprotein IIb/IIIa inhibitors are given together.', NULL, 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.', NULL, 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.2 times control. Bleeding precautions should be observed and patients should be closely monitored for clinical and laboratory evidence of adverse effects.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
