        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445983, 'Glucagon', 'Warfarin', 'Minor', 'Glucagon may increase the risk of bleeding in patients on oral anticoagulants. The mechanism is unknown. Frequent monitoring of the prothrombin time or INR is recommended.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
