    ) VALUES (1445988, 'Griseofulvin', 'Warfarin', 'Moderate', 'Griseofulvin has been reported to increase warfarin requirements in some previously stabilized patients. The mechanism may be induction of the hepatic metabolism of warfarin. Similar effects may occur with other oral anticoagulants.', NULL, 'Patients on oral anticoagulants should be monitored for alterations in INR whenever griseofulvin therapy is added, discontinued, or changed.', NULL, 'Patients on oral anticoagulants should be monitored for alterations in INR whenever griseofulvin therapy is added, discontinued, or changed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
