        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445997, 'Ifosfamide', 'Warfarin', 'Moderate', 'Limited data (from three patients) reveal enhanced anticoagulant activity (significantly increased INR) during chemotherapy with ifosfamide/mesna in patients previously stable on warfarin. The mechanism is not known, but is suspected to be displacement of warfarin from protein-binding sites by ifosfamide metabolites.', NULL, 'Careful and frequent monitoring of INR during coadministration is recommended.', NULL, 'Careful and frequent monitoring of INR during coadministration is recommended. Also, close monitoring is recommended when chemotherapeutic agents are discontinued, because anticoagulant effect may be decreased and the risk of thromboembolism increased. It may also be advisable to monitor patients on other anticoagulants.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
