        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1445999, 'Imatinib', 'Warfarin', 'Major', 'Data from human liver microsome studies indicate that imatinib is a potent competitive inhibitor of the CYP450 2C9 isoenzyme. Although no formal clinical interaction studies have been performed, the coadministration of imatinib and warfarin is likely to result in elevated plasma concentrations and increased pharmacologic effects of warfarin due to inhibition of CYP450 2C9-mediated metabolism.', NULL, 'The manufacturer recommends that patients treated with imatinib who require anticoagulation receive low molecular weight or standard heparin.', NULL, 'The manufacturer recommends that patients treated with imatinib who require anticoagulation receive low molecular weight or standard heparin.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
