        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446010, 'Isoniazid', 'Warfarin', 'Moderate', 'Isoniazid may increase the anticoagulant effect of warfarin, perhaps by inhibition of warfarin metabolism. Other oral anticoagulants may interact with isoniazid in a similar fashion. The proposed mechanism is inhibition of CYP450 2C9 hepatic metabolism by isoniazid.', NULL, 'The patient''s INR should be monitored, and the anticoagulant dosage should be adjusted if necessary, whenever isoniazid is initiated, discontinued or the dosage is changed.', NULL, 'The patient''s INR should be monitored, and the anticoagulant dosage should be adjusted if necessary, whenever isoniazid is initiated, discontinued or the dosage is changed. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bruising, red or brown urine, or red or black stools.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
