        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446022, 'Warfarin', 'Levamisole', 'Moderate', 'Significantly increased INR and prothrombin times have been reported in patients receiving concomitant warfarin, fluorouracil, and levamisole. Discontinuation of chemotherapy resulted in subtherapeutic anticoagulation. Reinstitution of chemotherapy resulted in a clinically significant elevation in the INR. The mechanism of this interaction is unknown and causality has not been clearly established.', NULL, 'If concomitant therapy is necessary, close monitoring of the INR or PT is recommended.', NULL, 'If concomitant therapy is necessary, close monitoring of the INR or PT is recommended. Anticoagulant dose adjustments may be necessary. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, red or brown urine, red or black stools, or unusual bleeding or bruising.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
