        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446033, 'Warfarin', 'Mechlorethamine', 'Minor', 'case report describes a 62-year-old man treated with warfarin whose prothrombin times increased by 50% to 100% during three cycles of a chemotherapeutic regimen consisting of cyclophosphamide, doxorubicin, etoposide, mechlorethamine, vincristine, procarbazine, methotrexate, and prednisone. He developed a subconjunctival hemorrhage on day 1 of the first cycle. The prothrombin time returned to normal within one week of discontinuation of warfarin. Due to the complexity of the chemotherapeutic regimen, a causal relationship to any of the medications or combination of medications cannot be established.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
