        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446051, 'Warfarin', 'Mirabegron', 'Minor', 'Coadministration with mirabegron has been shown to slightly alter the pharmacokinetics of single-dose warfarin. Mirabegron had no effect on warfarin pharmacodynamic endpoints such as International Normalized Ratio (INR) and prothrombin time. However, the effect of mirabegron on multiple doses of warfarin has not been fully investigated.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
