    ) VALUES (1446072, 'Warfarin', 'Olsalazine', 'Minor', 'Patients treated concomitantly with olsalazine and warfarin have experienced an increase in prothrombin time. The clinical significance is unknown. During concomitant therapy, the INR should be monitored. Other oral anticoagulants may interact with olsalazine in a similar manner.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
