    ) VALUES (1446083, 'Warfarin', 'Paraldehyde', 'Minor', 'Ethchlorvynol and paraldehyde may inhibit the hypoprothrombinemic response to warfarin due to hepatic enzyme induction. Monitor patient for altered anticoagulant effect if these drugs are added to or removed from a patient''s regimen.', NULL, '-', NULL, '-', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
