INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446160, 'Warfarin', 'Sulfinpyrazone', 'Major', 'Sulfinpyrazone inhibits platelet function and inhibits the metabolism of warfarin and nicoumalone, potentiating their hypoprothrombinemic effects. Severe bleeding episodes have been reported. This interaction is expected with other coumarin derivatives also.', 'DDInter', 1767317476, 'If coumarin derivatives must be used with sulfinpyrazone, the patient should be monitored for excessive anticoagulation. However, it is best to avoid this combination or reduce the anticoagulant dosage by 20% to 50%. Patients should be advised to notify their physician if they experience any signs of excessive anticoagulation, such as unusual or prolonged bleeding, bruising, vomiting, change in stool or urine color, headache, dizziness, or weakness.', 'Metabolism', 'If coumarin derivatives must be used with sulfinpyrazone, the patient should be monitored for excessive anticoagulation.', NULL, NULL, 'Major', 'pharmacodynamic');
