INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446249, 'Foscarnet', 'Kanamycin', 'Major', 'The potential nephrotoxicity of foscarnet and aminoglycosides may be additive. Concomitant use of these drugs could also lead to additive hypokalemia, hypomagnesemia, and hypocalcemia, which may increase the foscarnet-related risks of QT prolongation and seizures.', 'DDInter', 1767317476, 'The manufacturer recommends avoiding concomitant therapy unless the potential benefits outweigh the risks. Electrolyte levels and renal function of patients receiving these medications concomitantly should be monitored carefully, and dosages adjusted or discontinued if necessary.', 'Synergism', 'The manufacturer recommends avoiding concomitant therapy unless the potential benefits outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
