INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446251, 'Gallium nitrate', 'Kanamycin', 'Major', 'Concomitant use of gallium nitrate and aminoglycosides may increase the risk that severe renal insufficiency will develop in patients with hypercalcemia of malignancy.', 'DDInter', 1767317476, 'Adequate hydration and close monitoring of renal function are recommended. The manufacturer recommends discontinuing gallium nitrate before starting therapy with other nephrotoxic agents.', 'Others', 'Adequate hydration and close monitoring of renal function are recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446252, 'Gentamicin', 'Kanamycin', 'Moderate', 'Use of more than one aminoglycoside concurrently or sequentially may increase the risk of nephrotoxicity and ototoxicity. The risk is greatest in patients with impaired renal function, those receiving high doses for prolonged periods of time, elderly patients, and dehydrated patients. Ototoxicity may occur without renal toxicity. Oral neomycin undergoes systemic absorption and has also been associated with nephrotoxicity and ototoxicity.', 'DDInter', 1767317476, 'The use of more than one aminoglycoside should be avoided. Patients receiving aminoglycosides should be closely monitored for development of toxicity, including renal function tests and serial, vestibular, and audiometric tests. Ototoxicity may be permanent and may not be apparent until long after therapy has been discontinued. The onset of auditory toxicity may be asymptomatic or may manifest with early symptoms such as feelings of fullness in the ear, high tone hearing loss, or tinnitus. Signs of vestibulotoxicity include ataxia, tinnitus, and vertigo.', 'Synergism', 'The use of more than one aminoglycoside should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446253, 'Gentamicin (topical)', 'Kanamycin', 'Moderate', 'Topically applied aminoglycosides may be systemically absorbed and have potentially additive effects with systemically administered aminoglycosides. The risk of oto- and nephrotoxicity and other adverse effects of aminoglycosides may be increased.', 'DDInter', 1767317476, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.g., application to burnt or broken skin; use of large quantities or on large areas for prolonged periods). The normally recommended dosages of these products should not be exceeded.', 'Synergism', 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
