INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446270, 'Metocurine', 'Kanamycin', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', 'DDInter', 1767317476, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446271, 'Mezlocillin', 'Kanamycin', 'Moderate', 'INTERVAL: Mezlocillin may inactivate aminoglycosides in vivo and in vitro. The mechanism is complexation with the aminoglycoside. Typically, this effect is significant only in patients with renal failure or if the agents are mixed in the same IV container or line. Lower serum aminoglycoside levels may be seen, and dosage adjustments may be necessary.', 'DDInter', 1767317476, 'Mezlocillin and aminoglycosides should be administered separately during combined therapy.', 'Others', 'Mezlocillin and aminoglycosides should be administered separately during combined therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
