INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446294, 'Remdesivir', 'Kanamycin', 'Moderate', 'Coadministration of remdesivir with nephrotoxic agents may increase the plasma concentrations of its main metabolite, GS-441524, and the excipient, sulfobutylether-beta-cyclodextrin sodium (SBECD). Both GS-441524 and SBECD are primarily eliminated by the kidneys, thus renal impairment secondary to the use of nephrotoxic agents may reduce their clearance and increase the risk of accumulation.', 'DDInter', 1767317476, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function. Caution is advised if remdesivir is used in patients who have recently received potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function (estimated glomerular filtration rate, or eGFR, for adult and pediatric patients older than 28 days; serum creatinine for full-term neonates at least 7 days up to 28 days old) should be evaluated prior to starting remdesivir and monitored daily during treatment or as clinically appropriate.', 'Excretion', 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1446295, 'Human Rho(D) immune globulin', 'Kanamycin', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', 'DDInter', 1767317476, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
