        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446554, 'Aluminum hydroxide', 'Ketoconazole', 'Moderate', 'INTERVAL: The dissolution of oral ketoconazole requires an acidic environment. Medications that decrease the acidity of the stomach, such as omeprazole or H2 antagonists can reduce the bioavailability of ketoconazole by 75% to 80%. Antacids are also expected to impair ketoconazole absorption.', NULL, 'It is recommended to administer antacids at least 1 hour before or 2 hours after ketoconazole.', NULL, 'It is recommended to administer antacids at least 1 hour before or 2 hours after ketoconazole. Patients should be monitored for altered antifungal effects and the ketoconazole dosage adjusted if necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
