        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446555, 'Aminophylline', 'Ketoconazole', 'Minor', 'Coadministration of ketoconazole with a slow-release preparation of theophylline has been reported to decrease serum theophylline concentration. The mechanism may be related to decreased absorption. Ketoconazole has not been shown to interfere with the pharmacokinetics of intravenous theophylline. Theophylline dosages should be adjusted if necessary. Patients should be monitored for altered efficacy of theophylline if ketoconazole is added to their regimen.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
