        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1446657, 'Isoniazid', 'Ketoconazole', 'Moderate', 'The concomitant administration of potent CYP450 3A4 inducers may significantly reduce itraconazole and ketoconazole serum levels. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of the antifungal agents. Antifungal therapy may fail.', NULL, 'Coadministration of potent CYP450 3A4 inducers with itraconazole or ketoconazole should be avoided.', NULL, 'Coadministration of potent CYP450 3A4 inducers with itraconazole or ketoconazole should be avoided. Some authorities recommend avoiding concomitant therapy with potent CYP450 3A4 inducers from 2 weeks before and during treatment with itraconazole. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
