    ) VALUES (1446748, 'Oxybutynin', 'Ketoconazole', 'Moderate', 'INTERVAL: Theoretically, anticholinergic agents can reduce gastric acid secretion and reduce the oral bioavailability of ketoconazole, which requires an acidic environment for dissolution and absorption.', NULL, 'Anticholinergic agents should be administered at least 2 hours after ketoconazole.', NULL, 'Anticholinergic agents should be administered at least 2 hours after ketoconazole.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
