    ) VALUES (1446762, 'Perampanel', 'Ketoconazole', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of perampanel, which is primarily metabolized by the isoenzyme. In a clinical study, ketoconazole 400 mg daily for 10 days resulted in an increase in perampanel AUC by 20% and half-life by 15%.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
