    ) VALUES (1446887, 'Osimertinib', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. Reduced therapeutic efficacy of osimertinib should be anticipated.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inducers should generally be avoided. However, if concomitant use is required, some authorities recommend increasing the dose of osimertinib to 160 mg daily during treatment with the potent 3A4 inducer and resumed at 80 mg daily, 3 weeks after discontinuation of the potent 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
